import React from 'react'
import {shallow} from 'enzyme'
import UserImageSVG from './userImageSVG'

describe('LocationImage', () => {
    describe('default component', () => {
        const wrapper = shallow(
            <UserImageSVG />
        )

        const pathElement = wrapper.childAt(0)

        it('should be an SVG node', () => {
            expect(wrapper.type()).toBe('svg')
        })

        it('should have a single path', () => {
            expect(pathElement.type()).toBe('path')
        })

        it('should not have a fill color', () => {
            expect(wrapper.prop('fill')).toBe(undefined)
        })
    })
})
