import React from 'react'
import PropTypes from 'prop-types'
import Icon from '@abc/quarkicons'

const InlineHover = (props) => {
    const {children, icon, iconSize, onClick, ...remain} = props
    const childrenClasses = icon ? 'inline-hover__children' : 'inline-hover__children inline-hover__children--no-icon'

    return (
        <div
            className="inline-hover"
            onClick={onClick}
            data-abc-id="inlineHover"
            {...remain}
        >
            <div className={childrenClasses}>
                <div className="inline-hover__children__elements">{children}</div>
            </div>
            {icon &&
                <div className="inline-hover__icon">
                    <Icon type={icon} style={{fontSize: iconSize}} />
                </div>
            }
        </div>
    )
}

InlineHover.propTypes = {
    children: PropTypes.node.isRequired,
    onClick: PropTypes.func,
    icon: PropTypes.string,
    iconSize: PropTypes.string,
}

export default InlineHover