import React from 'react'
import {mount} from 'enzyme'
import Icon from '@abc/quarkicons'
import InlineHover from './'

describe('InlineHover', () => {
    describe('with minimum properties', () => {
        const inlineHover = mount(
            <InlineHover><h1>test</h1></InlineHover>
        )

        it('should have the class .inline-hover', () => {
            expect(inlineHover.hasClass('inline-hover')).toBe(true)
        })

        it('should have the data-abc-id prop inlineHover', () => {
            expect(inlineHover.find('.inline-hover').prop('data-abc-id')).toBe('inlineHover')
        })
    })

    describe('with an icon', () => {
        const inlineHover = mount(
            <InlineHover icon="eye"><h1>test</h1></InlineHover>
        )

        it('should have an icon in it somewhere', () => {
            expect(inlineHover.find(Icon).length).toBe(1)
        })
    })

    describe('with an icon and iconSize', () => {
        const inlineHover = mount(
            <InlineHover icon="eye" iconSize="2rem"><h1>test</h1></InlineHover>
        )

        it('should have an icon in it somewhere', () => {
            const inlineIcon = inlineHover.find('.inline-hover__icon')
            expect(inlineIcon.find(Icon).prop('style').fontSize).toBe('2rem')
        })
    })
})