import React, {Component} from 'react'
import PropTypes from 'prop-types'
import colClasses, {gridPropTypes} from '../../../utils/colClasses'

export default class Col extends Component {
    buildClasses(breakPoints, className) {
        const classes = colClasses(breakPoints)

        if (className) {
            classes.push(className)
        }

        return classes.join(' ')
    }

    render() {
        const {xs, sm, md, lg, xl, className, ...remain} = this.props

        const breakPoints = {xs, sm, md, lg, xl}
        const classNames = this.buildClasses(breakPoints, className)

        return (
            <div {...remain} className={classNames}>
                {this.props.children}
            </div>
        )
    }
}

Col.propTypes = {
    children: PropTypes.node,
    className: PropTypes.string,
    xs: gridPropTypes,
    sm: gridPropTypes,
    md: gridPropTypes,
    lg: gridPropTypes,
    xl: gridPropTypes,
}