import React, {Component} from 'react'
import PropTypes from 'prop-types'
import colClasses, {gridPropTypes} from '../../../utils/colClasses'

export default class Row extends Component {
    buildClasses(breakPoints, className) {
        let classes = ['row']
        classes = classes.concat(colClasses(breakPoints, true))

        if (className) {
            classes.push(className)
        }

        return classes.join(' ')
    }

    render() {
        const {xs, sm, md, lg, xl, className, ...remain} = this.props

        const breakPoints = {xs, sm, md, lg, xl}

        return (
            <div {...remain} className={this.buildClasses(breakPoints, className)}>
                {this.props.children}
            </div>
        )
    }
}

Row.propTypes = {
    children: PropTypes.node.isRequired,
    className: PropTypes.string,
    xs: gridPropTypes,
    sm: gridPropTypes,
    md: gridPropTypes,
    lg: gridPropTypes,
    xl: gridPropTypes,
}