import React from 'react'
import {mount} from 'enzyme'
import Modal, {transitionDirections, transitionTypes} from './'

describe('Modal', () => {
    describe('Default Component', () => {
        const wrapper = mount(
            <Modal isOpen={false} toggle={() => true} />
        )

        it('should have the prop type equal to basic', () => {
            expect(wrapper.prop('type')).toEqual('basic')
        })

        it('should have the prop autoFocus true by default', () => {
            expect(wrapper.prop('autoFocus')).toEqual(true)
        })

        it(`should have the prop transitionDirection equal to ${transitionDirections.LEFT}`, () => {
            expect(wrapper.prop('transitionDirection')).toEqual(transitionDirections.LEFT)
        })

        it(`should have the prop transitionType equal to ${transitionTypes.FLIP}`, () => {
            expect(wrapper.prop('transitionType')).toEqual(transitionTypes.FLIP)
        })
    })
})
