import React from 'react'
import {Row, Col, Tooltip} from '../../export'

const placementOptions = [
    'auto',
    'auto-start',
    'auto-end',
    'top',
    'top-start',
    'top-end',
    'right',
    'right-start',
    'right-end',
    'bottom',
    'bottom-start',
    'bottom-end',
    'left',
    'left-start',
    'left-end',
]

export default class TooltipExample extends React.Component {
    constructor(props) {
        super(props)

        const initialState = {}

        placementOptions.forEach((placement) => {
            initialState[placement] = false
        })

        this.state = initialState

        this.toggleTooltip = this.toggleTooltip.bind(this)
    }

    toggleTooltip(target) {
        this.setState({
            [target]: !this.state[target],
        })
    }

    render() {
        return (
            <div className="exampleSection">
                <a name="tooltips"/>
                <h2 className="mb-2">Tooltips</h2>
                <p>
                    These are the main <code>placement</code> options available, but more detailed control can be enforced through additional options
                    available in <a target="_blank" href="https://reactstrap.github.io/components/tooltips/">Reactrap</a>.
                </p>

                <Row className="mb-3 exampleSubsection">
                    {
                        placementOptions.map(placement => (
                            <Col key={`popover_${placement}`} sm={4}>
                                <p><a href="#tooltips" id={`tooltip_${placement}`}>{placement}</a></p>
                                <Tooltip
                                    placement={placement}
                                    isOpen={this.state[placement]}
                                    target={`tooltip_${placement}`}
                                    toggle={() => this.toggleTooltip(placement)}
                                >
                                    I'm a tooltip on the {placement}.
                                </Tooltip>
                            </Col>
                        ))
                    }
                </Row>
            </div>
        )
    }
}