import PropTypes from 'prop-types'
import React from 'react'
import styles from './icon.styl'
import icons from '../icons'

const iconMap = {
    'arrow-down': 'chevron-down',
    'arrow-down-circle': 'chevron-circle-down',
    'arrow-down-sm': 'chevron-down',
    'arrow-down-sm-circle': 'chevron-circle-down',
    'arrow-down-xs': 'chevron-down',
    'arrow-left': 'chevron-left',
    'arrow-left-circle': 'chevron-circle-left',
    'arrow-left-sm': 'chevron-left',
    'arrow-left-sm-circle': 'chevron-circle-left',
    'arrow-left-xs': 'chevron-left',
    'arrow-right': 'chevron-right',
    'arrow-right-circle': 'chevron-circle-right',
    'arrow-right-sm': 'chevron-right',
    'arrow-right-sm-circle': 'chevron-circle-right',
    'arrow-right-xs': 'chevron-right',
    'arrow-up': 'chevron-up',
    'arrow-up-circle': 'chevron-circle-up',
    'building': 'building',
    'building-solid': 'building-solid',
    'building-thin': 'building-thin',
    'checkmark': 'check',
    'checkmark-circle': 'check-circle',
    'email': 'envelope',
    'email-circle': 'envelope-circle',
    'email-thin': 'envelope',
    'gear': 'cog',
    'hand-paper': 'hand-paper',
    'hand-paper-solid': 'hand-paper-solid',
    'hand-paper-thin': 'hand-paper-thin',
    'location': 'map-marker',
    'location-circle': 'map-marker',
    'location-thin': 'map-marker-o',
    'phone': 'mobile',
    'phone-circle': 'mobile-circle',
    'phone-thin': 'mobile',
    'plus-sm': 'plus',
    'plus-sm-circle': 'plus-circle',
    'plus-xs': 'plus',
    'prospects': 'user-plus',
    'user-add': 'user-plus',
    'user-remove': 'user-times',
    'warning': 'exclamation-triangle-o',
    'warning-circle': 'exclamation-circle',
    'x-sm': 'x',
    'x-sm-circle': 'x-circle',
    'x-xs': 'x',
}

const Icon = (props) => {
    const {type, className, ...remain} = props
    let icon = `icon-${iconMap[type] || type}`
    icon += className ? ` ${className}` : ''

    return (
        <i className={icon} {...remain} />
    )
}

Icon.propTypes = {
    type: PropTypes.oneOf([
        ...Object.keys(iconMap),
        ...icons,
    ]).isRequired,
    className: PropTypes.string,
}

export default Icon