const devConfig = require('./webpack.dev.config')
const prodConfig = require('./webpack.prod.config')
const isProd = process.env.NODE_ENV === 'production'
const autoprefixer = require('autoprefixer')

const webpackConfig = (isProd) ? prodConfig : devConfig

module.exports = {
    entry: webpackConfig.entry,
    output: webpackConfig.output,
    module: {
        loaders: [
            {
                test: /\.js$/,
                exclude: /node_modules/,
                loader: 'babel-loader',
                query: {
                    presets: ['es2015', 'react'],
                    plugins: ['transform-object-rest-spread'],
                },
            },
            {
                test: /\.styl$/,
                loader: 'style-loader!css?modules&importLoaders=1&localIdentName=[local]!postcss?sourceMap=true!stylus-loader',
                exclude: /globalStyles/,
            },
            {
                test: /\.css$/,
                loader: 'style-loader!css-loader',
            },
            {
                test: /\.(eot|svg|ttf|woff|woff2)$/,
                loader: 'url-loader?name=fonts/[name].[ext]',
            },
        ],
    },
    externals: webpackConfig.externals,
    devServer: webpackConfig.devServer,
    devTool: webpackConfig.devTool,
    postcss: [autoprefixer],
    resolve: {
        alias: {
            fonts: './fonts',
        },
    },
}