const KEYCODE_V = 86
const KEYCODE_X = 88
const KEYCODE_Y = 89
const KEYCODE_Z = 90

export default {
    isCut: event => (event.ctrlKey || event.metaKey) && event.keyCode === KEYCODE_X,
    isPaste: event => (event.ctrlKey || event.metaKey) && event.keyCode === KEYCODE_V,
    isUndo: event => (
        (event.ctrlKey || event.metaKey) &&
        event.keyCode === (event.shiftKey ? KEYCODE_Y : KEYCODE_Z)
    ),
    isRedo: event => (
        (event.ctrlKey || event.metaKey) &&
        event.keyCode === (event.shiftKey ? KEYCODE_Z : KEYCODE_Y)
    ),
}
