import {validatePhone} from '../../export'

describe('validatePhone', () => {
    it('should be truthy for a 10-digit numeric string that is potentially valid', () => {
        expect(validatePhone('2134567890')).toBeTruthy()
    })
    it('should be falsy for a 10-digit numeric string that is obviously invalid (area code doesn\'t exist, 555, etc.)', () => {
        expect(validatePhone('1234567890')).toBeFalsy()
        expect(validatePhone('5552134567')).toBeFalsy()
    })
    it('should be falsy for a 9-digit numeric string', () => {
        expect(validatePhone('213456789'))
    })
    it('should be falsy for a 10-digit numeric string when not valid for a particular country', () => {
        expect(validatePhone('1234567890', 'UK')).toBeFalsy()
    })
})
