import {validateRequired} from '../../export'

describe('validateRequired', () => {
    it('should return true for a valid value', () => {
        expect(validateRequired('I\'m a value')).toBe(true)
    })
    it('should return true for a valid numeric value', () => {
        expect(validateRequired(0)).toBe(true)
    })
    it('should return false for null', () => {
        expect(validateRequired(null)).toBeFalsy()
    })
    it('should return false for undefined', () => {
        expect(validateRequired(undefined)).toBeFalsy()
    })
    it('should return false for an empty string', () => {
        expect(validateRequired('')).toBeFalsy()
    })
})
