import {territoryOptionsForCountry} from '../../utils/address'

const validateTerritory = (value, country) => {
    let response = false

    if (value && value.trim() !== '') {
        const territories = territoryOptionsForCountry(country)
        response = !!(territories[value]) || Object.keys(territories).some(key => territories[key] === value.trim())
    }

    return response
}

export const validateState = value => validateTerritory(value, 'US')

export default validateTerritory