import {validateTerritory, validateState} from '../../export'

describe('validateState', () => {
    const validStates = [
        'AR',
        'Arkansas',
    ]
    const invalidStates = [
        'WC',
        'West Carolina',
    ]

    validStates.forEach((territory) => {
        it(`should be truthy for '${territory}'`, () => {
            expect(validateState(territory)).toBeTruthy()
        })
    })

    invalidStates.forEach((territory) => {
        it(`should be falsy for '${territory}'`, () => {
            expect(validateState(territory)).toBeFalsy()
        })
    })
})

describe('validateTerritory', () => {
    const validStates = [
        'AR',
        'Arkansas',
    ]
    const validProvinces = [
        'BC',
        'British Columbia',
    ]

    it('should be falsy for an empty value', () => {
        expect(validateTerritory('', 'CA')).toBeFalsy()
    })

    it('should be falsy for an empty value and empty country', () => {
        expect(validateTerritory('', '')).toBeFalsy()
    })

    it('should be falsy for an empty country', () => {
        expect(validateTerritory('AR', '')).toBeFalsy()
    })

    it('should be falsy for an undefined country', () => {
        expect(validateTerritory('AR')).toBeFalsy()
    })

    validStates.forEach((territory) => {
        it(`should be truthy for '${territory}' in United States`, () => {
            expect(validateTerritory(territory, 'US')).toBeTruthy()
        })
    })

    validProvinces.forEach((territory) => {
        it(`should be truthy for '${territory}' in Canada`, () => {
            expect(validateTerritory(territory, 'CA')).toBeTruthy()
        })
    })

    validStates.forEach((territory) => {
        it(`should be falsy for '${territory}' in Canada`, () => {
            expect(validateTerritory(territory, 'CA')).toBeFalsy()
        })
    })

    validProvinces.forEach((territory) => {
        it(`should be falsy for '${territory}' in United States`, () => {
            expect(validateTerritory(territory, 'US')).toBeFalsy()
        })
    })
})
